package com.luc.calculator;import java.util.Scanner;public class Calculator {    int num_peop;    int income;    int loanAmount;    int years;    double interestRate;    int extraPayment;    public Calculator() {        System.out.println("Enter number of family members and their income: ");        Scanner calc = new Scanner(System.in);        this.num_peop = calc.nextInt();        this.income = calc.nextInt();    }    void calcPrint() {        double payment = (loanAmount * monthperc()) / (1 - Math.pow((1 + monthperc()), -1 * years * MONTH));        double startingBalance = loanAmount;        double endingBalance = startingBalance;        double monthlyinterest = startingBalance * monthperc();        double principal = payment - monthlyinterest;        double interestAmount = monthlyinterest + payment;        System.out.println("YOUR CREDIT = " + loanAmount);        System.out.println("MONTHLY PAYMENT = " + payment(loanAmount, monthperc()));        System.out.println("MONTH,  STARING_BALANCE,  PAYMENT,  INTEREST," +                "  PRINCIPAL,  ENDING_BALANCE,  TOTAL_INTEREST");        for (int i = 1; i < years * MONTH + 1; i++) {            if (endingBalance >= payment) {                if (i == 1) {                    System.out.printf("%d %15.2f %15.2f %15.2f %15.2f %15.2f %15.2f%n", i, startingBalance,                            payment, monthlyinterest, principal, endingBalance, interestAmount);                } else {                    payment = payment + extraPayment;                    startingBalance = startingBalance - principal;                    monthlyinterest = startingBalance * monthperc();                    principal = payment - monthlyinterest;                    endingBalance = startingBalance - principal;                    interestAmount = monthlyinterest + payment;                    System.out.printf("%d %15.2f %15.2f %15.2f %15.2f %15.2f %15.2f%n", i, startingBalance,                            payment, monthlyinterest, principal, endingBalance, interestAmount);                }            } else {                startingBalance = endingBalance;                System.out.printf("%d %15.2f %15.2f %15.2f,%15.2f %15.2f %15.2f%n", i, startingBalance,                        payment, monthlyinterest, principal, endingBalance, interestAmount);                break;            }        }    }    final int MONTH = 12;    double monthperc() {        return interestRate / 100 / MONTH;    }    double payment(int loanAmount, double monthperc) {        return (loanAmount * monthperc) / (1 - Math.pow((1 + monthperc), -1 * years * MONTH));    }}