package com.luc.calculator;import java.util.Scanner;public class Mortgage {    public static void main(String[] args) {        Calculator calc1 = new Calculator();        System.out.println("Enter credit, credit years, percent, extra payment: ");        Scanner calc = new Scanner(System.in);        calc1.loanAmount = calc.nextInt();        calc1.years = calc.nextInt();        calc1.interestRate = calc.nextInt();        calc1.extraPayment = calc.nextInt();        double month = calc1.monthperc();        double monthlyPayment = calc1.payment(calc1.loanAmount, month);        if ((calc1.num_peop == 1 && monthlyPayment <= calc1.income * 0.5)                || (calc1.num_peop == 2 && monthlyPayment <= calc1.income * 0.45)                || (calc1.num_peop == 3 && monthlyPayment <= calc1.income * 0.35)                || (calc1.num_peop == 4 && monthlyPayment <= calc1.income * 0.3)                || (calc1.num_peop == 5 && monthlyPayment <= calc1.income * 0.25)) {            calc1.calcPrint();        } else {            System.out.println("You can't get a credit!");        }    }}